import React, { Component } from 'react';
import { v4 as uuid } from 'uuid';
import {
  Box,
  Container,
  makeStyles,
  Button,
  Grid,
  Card
} from '@material-ui/core';
import Page from 'src/components/Page';
import Results from './Results';
import Toolbar from './Toolbar';
import Script from 'react-load-script'
//import data from './data';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    minHeight: '100%',
    paddingBottom: theme.spacing(3),
    paddingTop: theme.spacing(3)
  }
}));
//const classes = useStyles();

//customers = []
export default class CustomerListView extends Component {

  constructor(props){
    super(props);
    this.state = {
      customers: [],
      clicked_keyword: "",
      datafeature: [],
      mlacurracy: []
    }
    this.handler = this.handler.bind(this)
  }
  handler(keyword){
    this.setState({
      clicked_keyword: keyword
    })
    console.log("clicked sds", this)


  }


  //console.log("hello")
  async componentDidMount() {
     fetch('http://localhost:4000/products')
      .then(response => response.json())
      .then(data => {this.setState({customers:data.data.map(
        customer => {
          return{
            id: uuid(),
            rank: customer.rank,
            keyword: customer.keyword,
            gender: customer.gender,
            date_: customer.date_,
            score: customer.score
          } 
        }

      )})})



  }



  render(){
    if(this.state["clicked_keyword"] == ""){
    return (

        <Container maxWidth={false}>
          <Toolbar />
          <Box mt={3}>

            <Results customers={this.state["customers"]} handler={this.handler} clicked_keyword={this.state["clicked_keyword"]} />
            {console.log(this.state["customers"], "keyword:",this.state["clicked_keyword"])}
          </Box>
        </Container>


    );
    }
    else{

      const cardStyle = {
        display: 'block',
        //width: '30vw',
        transitionDuration: '0.3s',
        height: '15vw'
    }
        /*{<Button>
        {this.state.clicked_keyword}

      </Button>}*/
      return(
      <Container maxWidth={false}>
        <Grid
         container
         spacing={5}
         >
           <Grid
              item
              lg={12}
              sm={12}
              xl={12}
              xs={12}
            >
              <Card style={{
                 display: 'block',
                 //width: '30vw',
                 transitionDuration: '0.3s',
                 height: '15vw',
                 backgroundColor: "blue" 
              }}>
                  {this.state.clicked_keyword}
              </Card>
            </Grid>
            <Grid
              item
              lg={12}
              sm={12}
              xl={12}
              xs={12}
            >
              <Card style={cardStyle}>
                  1. 검색 노출도
              </Card>
            </Grid>

            <Grid
              item
              lg={12}
              sm={12}
              xl={12}
              xs={12}
            >
              <Card style={cardStyle}>
                2. 안정성
              </Card>
            </Grid>

            <Grid
              item
              lg={12}
              sm={12}
              xl={12}
              xs={12}
            >
              <Card style={cardStyle}>
                3. 주기성
              </Card>
            </Grid>

            <Grid
              item
              lg={12}
              sm={12}
              xl={12}
              xs={12}
            >
              <Card style={cardStyle}>
                4. 추세
              </Card>
            </Grid>

        </Grid>
      </Container>
      );
    }
  }
};

//export default CustomerListView;
